  //     })
  //   }
  // )
  return (
    <div className="card">
      <div className="card__playerPic">
        <div className="card__playerPic--gradient"></div>
        <img src={props.actionShot} />
      </div>
      <div className="card__playerInfo">
        <div className="card__playerInfoBanner">
          <span>#{props.number}</span>
          |
          <span>{props.name}</span>
          |
          <span>{props.position}</span>
        </div>

      </div>
      <style jsx>{`
        .card {
          height: 350px;
          width: 250px;
          overflow: hidden;
          font-family: sans-serif;
        }
        .card__playerPic {
          width: 100%;
          height: 200px;
          overflow: hidden;
        }
        .card__playerPic--gradient {
          z-index:999;
          background: linear-gradient(to bottom, rgba(255,255,255,1) 0%,rgba(255,255,255,0) 100%);
        }
        img {
          object-fit: cover;
          object-position: center;
          width: 100%;
          height: 100%;
        }
        .card__playerInfoBanner{
          text-transform: uppercase;
          display: flex;
          justify-content: space-evenly;
          width:100%;
        }
        .card__playerInfoBanner span {
          color: #454545;
        }
        .card__playerInfo {
          display: flex;
        }
      `}</style>
    </div>
  )
}

export default Card
